struct Rect {
	vec2 pos,
         ext;
    mat2 kord;
    vec4 col;

};


Rect 
     R1 = Rect ( vec2(0.), vec2 (1.,1.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R5 = Rect ( vec2(0.), vec2 (2.,2.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R6 = Rect ( vec2(0.), vec2 (2.,2.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R7 = Rect ( vec2(0.), vec2 (2.,2.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R9 = Rect ( vec2(-1.45,0.), vec2 (.5,.5), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R10 = Rect ( vec2(-1.45,0.), vec2 (.5,.5), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R4 = Rect ( vec2(0.), vec2 (.5,.7), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R3 = Rect ( vec2(0., -.15), vec2 (.2,.2), mat2 (1.,0.,0.,1.), vec4 (-.2,-.2,-.2,4.)),
     R8 = Rect ( vec2(.6*sin(iDate.a/30.*3.14),.6*cos(iDate.a/30.*3.14)), vec2 (.2,.2), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
   	 R2 = Rect ( vec2(0., -.15), vec2 (.4,.7), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.));




mat2 Rota (in float alpha){
	float s = sin(alpha),
          c = cos(alpha);

   return mat2 (c, -s, s, c);

}


void Rotarect (in float alpha, inout Rect r_p){

    r_p.kord = Rota (alpha) * r_p.kord;


}




vec4 intersec (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x || abs(mat.y) >.5*reco.ext.y){
        return vec4 (0.);
    }

     if (mat.x*mat.x+mat.y*mat.y < .008)
          return vec4(0.);
     if (mat.x*mat.x+mat.y*mat.y < .02)
          return reco.col + vec4(p_uv.x,p_uv.y, p_uv.y+p_uv.x,1.);
     if (abs(mat.x) <.5*reco.ext.x && mat.y >.0 ){


       	 if (mat.x + mat.y <.4 && -mat.x + mat.y < .4)
                return vec4(0.);
         if ( mat.x + mat.y <.5 && -mat.x + mat.y < .5)
                return reco.col + vec4(p_uv.x,p_uv.y, p_uv.y+p_uv.x,1.);}
        return vec4(0.);
}


