void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.xy-0.5)*vec2 ( iResolution.x/iResolution.y, 1.);
	fragColor = vec4(0.);

    float h = iDate.a/(3600.);
    float m = iDate.a/(60.);
    float a = sin(3.14*iTime);

    if (a < 0.){
        Rotarect (3.14*iTime-3.14/2., R2);
    }
     else   Rotarect(-3.14*iTime-3.14/2.,R2);


    Rotarect (-(m/30.)*3.14, R1);

        if (h < 11.){
        Rotarect (-((h-12.)/6.)*3.14, R4);
    }
     else   Rotarect (-(h/6.)*3.14, R4);



	Rotarect (3.14/3., R6);
    Rotarect (3.14/6., R7);

    vec4 r1col;
    if (h<11.999999){
    r1col = intersec (uv,R1) + intersec2 (uv, R2)+ intersec3 (uv, R3)+intersec4(uv,R4)+ intersec5(uv,R5)+intersec5(uv,R6)+intersec5(uv,R7)+intersec6(uv, R8)+intersec8(uv,R10);
    }
    else 
    r1col = intersec (uv,R1) + intersec2 (uv, R2)+ intersec3 (uv, R3)+intersec4(uv,R4)+ intersec5(uv,R5)+intersec5(uv,R6)+intersec5(uv,R7)+intersec6(uv, R8)+intersec7(uv,R9);
    ;

    if (r1col.a < 1.){
        fragColor = vec4(.5-.21*uv.x,.35+.05*uv.y,.35+.2*uv.x-.1*uv.y,.1);

    }
        else  

            if ( h < 11.99999){
   fragColor = intersec (uv,R1)  + intersec2 (uv, R2) + intersec3 (uv,R3)+intersec4 (uv, R4)+intersec5(uv,R5)+intersec5(uv,R6)+intersec5(uv,R7)+intersec6(uv, R8)+intersec8(uv, R10);
            }else
   fragColor = intersec (uv,R1)  + intersec2 (uv, R2) + intersec3 (uv,R3)+intersec4 (uv, R4)+intersec5(uv,R5)+intersec5(uv,R6)+intersec5(uv,R7)+intersec6(uv, R8)+intersec7(uv, R9);
}
