bool isInCircle(vec2 center, float radius, vec2 point) {
	return length(point - center) < radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy * 0.5;
    float radius = iResolution.y * 0.3;
    vec2 center1 = center + vec2(0.5 * radius, 0);
    vec2 center2 = center - vec2(0.5 * radius, 0);
    vec4 white = vec4(1, 1, 1, 1);
    vec4 black = vec4(0, 0, 0, 1);
    if (isInCircle(center, radius, fragCoord)) {
		if (isInCircle(center1, radius * 0.15, fragCoord)) {
  		  	fragColor = black;
		} else if (isInCircle(center2, radius * 0.15, fragCoord)) {
			fragColor = white;
		} else {
            if (isInCircle(center1, radius * 0.5, fragCoord)) {
            	fragColor = white;
            } else if (isInCircle(center2, radius * 0.5, fragCoord)) {
            	fragColor = black;
            } else {
                if (fragCoord.y > center.y) {
                	fragColor = white;
                } else {
                	fragColor = black;
                }
            }
		}

    } else {
    	fragColor = vec4(0.5, 0.5, 0.5, 1);
    }
}
