vec3 nat2rgb(vec3 nat)
{
    if(nat.x>=0.0 && nat.x<0.25) //red to yellow
    {
        vec3 uns = mix(vec3(0.8,0.0,0.2),vec3(1.0,0.8,0.0),nat.x*4.0);
        return mix(vec3(nat.z),uns,nat.y);
    }
    else if(nat.x>=0.25 && nat.x<0.5) //yellow to green
    {
        vec3 uns = mix(vec3(1.0,0.8,0.0),vec3(0.0,0.6,0.4),(nat.x-0.25)*4.0);
        return mix(vec3(nat.z),uns,nat.y);
    }
    else if(nat.x>=0.5 && nat.x<0.75) //green to blue
    {
        vec3 uns = mix(vec3(0.0,0.6,0.4),vec3(0.0,0.6,0.8),(nat.x-0.5)*4.0);
        return mix(vec3(nat.z),uns,nat.y);
    }
    else //blue to red
    {
        vec3 uns = mix(vec3(0.0,0.6,0.8),vec3(0.8,0.0,0.2),(nat.x-0.75)*4.0);
        return mix(vec3(nat.z),uns,nat.y);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 nat1 = vec3(0.0,1.0,0.0);
    vec3 nat2 = vec3(0.75,1.0,1.0);
    vec3 rgb = mix(nat2rgb(nat1),nat2rgb(nat2),fragCoord.x/iResolution.x);
	fragColor = vec4(rgb,1.0);
}
