void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float correct;
    vec2 point;
    float dotWdth;
    float dotHeight;
    dotHeight = 20.0;
    dotWdth = 20.0;
    point = vec2(iMouse.x,iMouse.y);
    if (fragCoord.x <= point.x + dotWdth && fragCoord.x >= point.x - dotWdth) { 
        if (fragCoord.y <= point.y + dotHeight && fragCoord.y >= point.y - dotHeight) { correct = 1.0;}}
    if (correct == 1.0) { 	fragColor = vec4(sin(point.x / point.y),0.5*sin(point.x / point.y),0.7*sin(iTime),1.0); }
}
