#define PI 3.1415926535
#define clamps(x) clamp(x,0.,1.)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy)-.5;
    uv.x *= iResolution.x / iResolution.y;
    float time = iTime*.35;

    uv *= 13.;
    float atans = (atan(fract(uv.x)-.5,fract(uv.y)-.5)+PI)/(PI*2.);
    float checker = abs(floor(mod(uv.x,2.))-floor(mod(uv.y,2.))); //abs() was a quicker way for difference.
    float rotations = (checker==1.)?fract((atans+time)*2.):fract(((1.-atans)+time)*2.);
    float sharp = 90.;
    sharp *= (length(fract(uv)-.5))*2.;
    //float a = step(rotations,.5);
    float a = clamps((rotations-.5)*sharp);
    a += clamps(1.-((rotations)*sharp));

	fragColor = vec4(mix(vec3(1.),vec3(0.,0.5,.9),a),1.0);
}
