struct Rect {
	vec2 pos,
         ext;
    mat2 kord;
    vec4 col;

};


Rect 
     R1 = Rect ( vec2(-1.29,.0), vec2 (.9,.9), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R2 = Rect ( vec2(.0,.0), vec2 (.9,.9), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.)),
     R3 = Rect ( vec2(1.29,.0), vec2 (1.,1.), mat2 (1.,0.,0.,1.), vec4 (.9,.99,.80,4.));



mat2 Rota (in float alpha){
	float s = sin(alpha),
          c = cos(alpha);

   return mat2 (c, -s, s, c);

}


void Rotarect (in float alpha, inout Rect r_p){

    r_p.kord = Rota (alpha) * r_p.kord;


}



vec4 intersec (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x*abs(sin(iTime+1.55)) || abs(mat.y) >.5*reco.ext.y*cos(iTime)){
        return vec4 (0.);
    }
        if ( abs(mat.x) < .25 && mat.y < -.30)
            return vec4 (.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
           if (mat.x >.25 && abs(mat.y) <.1)
            if (abs(mat.x) +abs( mat.y) <.4)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
    	if ( mat.x < -.3 && abs(mat.y) < .50)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);

   return vec4 (0.);
}

vec4 intersec2 (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x*abs(sin(iTime+1.55)) || abs(mat.y) >.5*reco.ext.y*cos(iTime)){
        return vec4 (.0);
    }
        if ( abs(mat.x) < .3 && mat.y < -.30)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);

    	if ( mat.x < -.3 && abs(mat.y) < .50)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
    	if ( abs(mat.x) < .25 && abs(mat.y) < .055)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
    	if (mat.x >.3 && abs(mat.y) <.08)
            if (abs(mat.x) +abs( mat.y) <.4)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
    	if ( abs(mat.x) < .3 && mat.y > .30)
            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
   return vec4 (0.);
}

vec4 intersec3 (in vec2 p_uv, in Rect reco){
	vec2 xynew = p_uv - reco.pos;
    vec2 mat = reco.kord * xynew;

    if ( abs(mat.x) >.5*reco.ext.x*abs(sin(iTime+1.55)) || abs(mat.y) >.5*reco.ext.y*cos(iTime)){
        return vec4 (.0);
    }
        if (abs(2.*mat.x)+mat.y<.5)
         	if (abs(2.*mat.x)+mat.y>.2 || abs(mat.x) < .3 && abs(mat.y) < .055)

            return vec4(.9-.2*(mat.x-mat.y),.99-.2*(mat.x-mat.y),.80-.2*(mat.x-mat.y),1.);
   return vec4 (0.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.xy-0.5)*vec2 ( iResolution.x/iResolution.y, 1.);
	fragColor = vec4(0.);

	Rotarect (iTime, R1);
    Rotarect (iTime, R2);
    Rotarect (iTime, R3);



    vec4 r1col = intersec (uv,R1) + intersec2 (uv,R2)+ intersec3 (uv, R3);
    if (r1col.a < 1.){
        fragColor = vec4(.5,.25,.75,.1);}
    else   
   fragColor = intersec (uv,R1) + intersec2 (uv,R2) +intersec3 (uv,R3);



}
