void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / (iResolution.xy);
    uv = uv * 4.0 - 2.0;
    uv.y += 0.3;

    float timeframe = sin(iTime) + 2.0;
    if (pow(uv.x, 2.0) + pow((uv.y - pow(pow(uv.x, 2.0), 1.0/3.0)), 2.0) < timeframe){
    	fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
		fragColor = vec4(0.0, cos(uv.y - uv.x), sin(uv.y - uv.x), 1.0);
    }
}
