float distLine(vec3 ro, vec3 rd, vec3 p) {
    float d = length(cross(p-ro, rd))/length(rd);
    d = smoothstep(.06, .05, d);
	return d;
}

float dotList(vec3 ro, vec3 rd, vec3 p) {
    return acos(dot(normalize(p-ro), normalize(rd)));
}

float box(vec3 ro, vec3 rd){
    float d = 0.;
    d += distLine(ro, rd, vec3(0., 0., 0.));
    d += distLine(ro, rd, vec3(0., 1., 0.));
    d += distLine(ro, rd, vec3(1., 1., 0.));
    d += distLine(ro, rd, vec3(1., 0., 0.));
    d += distLine(ro, rd, vec3(0., 0., 1.));
    d += distLine(ro, rd, vec3(0., 1., 1.));
    d += distLine(ro, rd, vec3(1., 1., 1.));
    d += distLine(ro, rd, vec3(1., 0., 1.));

	return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 lookAt = vec3(.5);
    vec3 ro = vec3(3.* sin(t), 2. * cos(t), -3.);
    float zoom = 1.;

    vec3 f = normalize(lookAt - ro);
    vec3 r = cross(vec3(0., -1., 0.), f);
    vec3 u = cross(r, f);
    vec3 c = ro + f * zoom;
    vec3 i = c + uv.x * r + uv.y * u;
    vec3 rd = i - ro;


    float d = box(ro, rd);

    fragColor = vec4(d);
}
