const int NUM_SAMPLES = 7;

vec4 f(vec2 uv, float t, float i)
{
	float life = fract(i / float(NUM_SAMPLES) + t * .13);

	float fade = pow(life, 1.75) * (1.0 - pow(life, 16.0));
    vec4 color = 8.0 * fade * vec4(fract(i * 0.33), fract(0.33 + i * 0.33), fract(0.67 + i * 0.33), 1.0); 

    float s = 1.5 - 1.5 * life;
    float r = i * 2.73 + t * (0.05 - 0.025);
    vec2 cs = cos(r + vec2(0.0, -0.5) * 3.14); 
    mat2 m = mat2(
        cs.x, -cs.y,
        cs.y, cs.x);

    float samp1 = texture(iChannel0, (0.5 + m * uv * s)).r;
    float samp2 = texture(iChannel0, (0.5 + m * (uv - vec2(0.0, 0.05)) * s)).r;
	float samp3 = texture(iChannel0, (0.5 + m * (uv - vec2(0.0, 0.025)) * s)).r;
	float samp4 = texture(iChannel0, (0.5 + m * (uv - vec2(0.0, 0.0125)) * s)).r;

	return samp1 * 
		(1.0 + (samp2 - samp1) * 8.0) * 
		(1.0 + (samp3 - samp1) * 7.0) * 
		(1.0 + (samp4 - samp1) * 6.0) * 
        color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    vec4 sum = vec4(0.0);
    for (int i = 0; i < NUM_SAMPLES; ++i)
        sum += f(uv, iTime, float(i));    
    fragColor = pow(sum, vec4(1.35));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 cloud =
        texture(iChannel0, uv * 0.25) * 0.5 +
        texture(iChannel0, uv * 0.5) * 0.25 +
        texture(iChannel0, uv) * 0.125 +
        texture(iChannel0, uv * 2.0) * 0.0625 +
        texture(iChannel0, uv * 4.0) * 0.03125 +
        texture(iChannel0, uv * 8.0) * 0.015625;
    fragColor = pow(max(vec4(0.0), (1.0 - length(uv - 0.5) * 2.0) * cloud), vec4(4.0));
}
