#define PIXEL_SIZE 5.0
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy / iResolution.xy) * iResolution.xy;
    vec3 color;

    float plx = iResolution.x * PIXEL_SIZE / 640.0;
    float ply = iResolution.y * PIXEL_SIZE / 360.0;

    vec2 tlPos = floor(uv / vec2(plx, ply));
    tlPos *= vec2(plx, ply);
    vec2 blPos = tlPos;
    blPos.y += (ply - 1.0);	// To create cross stiching
    if (
        (mod(uv.x, plx) == mod(uv.y, ply)) || 
        (((uv.x - blPos.x) == (blPos.y - uv.y)))
    ) {
        color = vec3(0.0, 0.0, 0.0);	// Cross stiching color
    } else {
    	color = texture(iChannel0, tlPos * vec2(1.0/iResolution.xy)).xyz;
    }
    fragColor = vec4(color, 1.0);
}
