#define SAMPLES 2

float nrand( vec2 n )
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453)*0.9;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;  

    vec4 result = vec4(0.);

    for (int i=0; i<SAMPLES; i++)
    {
        //vec2 offset = vec2(sin(iTime*5.+float(i)*1.+fragCoord.x*30.),sin(iTime*3.+float(i)*10.+fragCoord.y*21.))*10. / iResolution.xy;

        vec2 addition = vec2(float(i), iTime/4000.);

        vec2 offset = (vec2(0.5-nrand(fragCoord.xy+addition),0.3-nrand(fragCoord.yx+addition)))/iResolution.xy*20.;

        //offset += sin(uv*700.)/300.;

        float angle = 6.243*float(i)/float(SAMPLES);

        //vec2 offset = sin(vec2(nrand(fragCoord.x+addition)+angle,nrand(fragCoord.y+addition)+angle))/100.;

        result += texture(iChannel0, uv + offset)*0.96/float(SAMPLES);
    }

    fragColor = result + texture(iChannel1, uv)*0.15;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 pixel = texture(iChannel0, uv);
    float opaque = smoothstep(.0, 0.7, distance(pixel.rgb, vec3(0.0471,0.6353,0.1451)));

    fragColor = texture(iChannel0, uv) * opaque;
}
