void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec4 c0 = texture(iChannel0, fragCoord.xy / iChannelResolution[0].xy);
    vec4 c1 = texture(iChannel1, fragCoord.xy / iChannelResolution[1].xy);
    vec4 c2 = texture(iChannel2, fragCoord.xy / iChannelResolution[2].xy);
	fragColor = c1 + c0 + c2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iChannelResolution[0].xy;
    uv.x += iTime / 2.0;    
    uv /= 2.0;
    vec4 t = texture(iChannel0, uv);
    float c = t.r > 0.999995 ? 1.0 : 0.0;
	fragColor = vec4(c, c, c, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
