// inspired from https://www.youtube.com/watch?v=Jri0del_6t4
// variant of https://www.shadertoy.com/view/XdVXWd 
//        and https://www.shadertoy.com/view/XdKSDd

// Gabor kernel
#define G(U,a,v) exp(-.5*dot(U,U)*1e3) * sin( 100.*dot(U,sin(a+vec2(1.57,0)))-10.*(v)*sin(iDate.w) ) 


void mainImage( out vec4 O, vec2 U )
{
	U = (U+U - (O.xy=iResolution.xy) ) / O.y;
    O -= O;


    for (float i=-6.; i<7.; i++)   
        for (float j=-6.; j<7.; j++) {
            float k = abs(i) < abs(j) ? i : j;
            O +=   G( U-vec2(i*.15 , j*.15), (k==j? 0. :1.57)  , k*(2.*mod(i+j-k,2.)-1.));
        }

	O = .5 + .5*O; 
}
