// variant of https://www.shadertoy.com/view/MdVyDR and https://shadertoy.com/view/MdVyDR 

void mainImage(out vec4 O, vec2 u) {
    for ( vec2 R = iResolution.xy, U = ( u+u - R ) / R.y, V,
               A = 4./(1.+sin(iTime)) +R-R;
          V = pow(U=abs(R=U),A), O.w++<9. && pow(V+V.y,1./A).x<1.;
          O += vec4(.11,.14,.2,0)
        ) U = sign(R)*(U/.4-1.) * mat2(cos( iTime + vec4(0,55,33,0))); } 





