void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.0/exp(iTime/3.0);
    vec2 uv = fragCoord/iResolution.y;

    vec2 initialValue = (uv-0.5)*3.0*zoom+vec2(
        -0.36,
        0.636005
    );
    vec2 currentValue = initialValue;

    float i;
    for(i = 0.0; i < 2000.0; i++){
        currentValue = mat2(currentValue,-currentValue.y,currentValue.x ) * currentValue + initialValue;
        if(length(currentValue) > 2.0){
            break;
        }
    }

    if(i >= 2000.0){
        fragColor = vec4(1.0);
    }
    else{
        fragColor = vec4(
            min(i/500.0, 1.0), 
            max(1.0-(i/200.0), 0.0),
            0.0,
            1.0
        );
    }
}
