#define PIXEL_SIZE_WIDTH 10.0
#define PIXEL_SIZE_HEIGHT 10.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Pixelation algorithm
    vec2 dd = vec2(PIXEL_SIZE_WIDTH, PIXEL_SIZE_HEIGHT) / iResolution.xy;
    vec2 coord = dd * floor(uv / dd);

    // Output to screen
    fragColor = texture(iChannel0, coord);
}
