void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4( fractNoise( uv.x, uv.y, 3 + (iFrame/2) % 6 ) );
}

float fractNoise( float a, float b, int n )
{
    float t;
    float v;
    for(int i=0; i < n; i++)
    {
        t = 0.111 * a + 1.123 * fract(123.456 * b);
        v = 0.999 * b - 3.456 * fract(456.123 * a);
        a = t;
        b = -v;
    }

    return fract(a * b);
}

vec2 mainSound( in int samp, float time )
{
    return vec2( fractNoise(time, time, 5) );
}
