#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UIF (1.0 / float(0xffffffffU))

vec2 hash21(float p) {
	uvec2 n = uint(int(p)) * UI2;
	n = (n.x ^ n.y) * UI2;
	return vec2(n) * UIF;
}

#define mainImage(O,u)\
    vec2 R = iResolution.xy, U = u, p = (U+U - R) / R.y;\
    O = vec2(.997,1).xyyy*texelFetch(iChannel0, ivec2(U), 0);\
    for(float i = 0.; i < R.x; i++) {\
        vec2 d = hash21(R.x*(iTime+i))*2.-1.;\
        if(length(d)-1. < 0.)\
            O.xy += vec2(.1*clamp(1. - 1.2e2*length(p-d), 0., 1.), 1);\
    }\
    O.z = 4. * O.y/(float(iFrame)*R.x);
