#define FEED_RATE r * 0.05
#define KILL_RATE r * 0.15
#define DIFF vec2(0.4,0.1)
#define ZOOM 1.0

float getChannel(sampler2D sam, vec2 uv)
{
    vec4 c = texture(sam,uv);
    return max(min(c.r, c.g), min(max(c.r, c.g), c.b)); // https://github.com/Chlumsky/msdfgen
}

float pn(vec3 p) 
{
   vec3 i = floor(p);
   vec4 a = dot(i, vec3(1., 57., 21.)) + vec4(0., 57., 21., 78.);
   vec3 f = cos((p-i)*3.14159)*(-.5) + .5;
   a = mix(sin(cos(a)*a), sin(cos(1.+a)*(1.+a)), f.x);
   a.xy = mix(a.xz, a.yw, f.y);
   return mix(a.x, a.y, f.z);
}

vec2 cell(vec2 fragCoord, vec2 pixel, vec2 dir, float scale, sampler2D sam, vec2 size)
{
    pixel *= dir;

    // remove screen border of domain
    if (fragCoord.x + pixel.x > size.x) fragCoord.x = 0.;
    if (fragCoord.y + pixel.y > size.y) fragCoord.y = 0.;
    if (fragCoord.x + pixel.x < 0.0) fragCoord.x = size.x;
    if (fragCoord.y + pixel.y < 0.0) fragCoord.y = size.y;

	vec2 uv = (fragCoord + pixel) / size.xy;
    return texture(sam, uv).rg * scale;
}

vec2 laplacian2D(vec2 fragCoord, vec2 dir, float a, float b, sampler2D sam, vec2 size) 
{
    float st = 1.;
    a /= 4.;
    b /= 4.;
    return 
        cell(fragCoord, vec2(0., -st), dir, a, sam, size) +
        cell(fragCoord, vec2(0., st), dir, a, sam, size) +
        cell(fragCoord, vec2(st, 0.), dir, a, sam, size) +
        cell(fragCoord, vec2(-st, 0.), dir, a, sam, size) +
        cell(fragCoord, vec2(-st, -st), dir, b, sam, size) +
        cell(fragCoord, vec2(-st, st), dir, b, sam, size) +
        cell(fragCoord, vec2(st, -st), dir, b, sam, size) +
        cell(fragCoord, vec2(st, st), dir, b, sam, size) -
        cell(fragCoord, vec2(0., 0.), dir, 1., sam, size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    fragColor = vec4(0,0,0,0);

   	if (iFrame < 120)
    {
        vec2 v = (fragCoord*2.-iResolution.xy)/iResolution.y;
        fragColor = vec4(step(length(v+vec2(1.5,0)),0.01));
    }
    else if (iMouse.z > 0.0)
	{
		vec2 mo = iMouse.xy / iResolution.xy / ZOOM;
    	fragColor = texture(iChannel0, uv);
        if (length(uv-mo) < 0.003)
			fragColor = mix(vec4(0,1,0,1), vec4(1,0,0,1), length(uv-mo)); 
	}
    else
    {
        float a = float(iFrame) * 0.1;

		vec2 dir = normalize(vec2(a+cos(a),a-sin(a)));

        dir = vec2(1);

        float r = getChannel(iChannel1, uv);

        vec2 diffusionCoef = DIFF;
        float feedCoef = FEED_RATE;
        float killCoef = KILL_RATE;

        vec2 ab = cell(fragCoord, vec2(0,0), vec2(0,0), 1., iChannel0, iResolution.xy);
        vec2 lp = laplacian2D(fragCoord, dir, .5, .5, iChannel0, iResolution.xy);

        float reaction = ab.x * ab.y * ab.y;
        vec2 diffusion = diffusionCoef * lp;
        float feed = feedCoef * (1. - ab.x);
        float kill = (feedCoef + killCoef) * ab.y;

        ab += diffusion + vec2(feed - reaction, reaction - kill);

    	fragColor = vec4(clamp(ab,0.,1e4),ab*r);
    }
}

