    vec2 U = u/iResolution.xy; U.x-=.5;
    O = abs(.07*vec4(6,5,4,3) - length(U));
    O = vec4(2.-6.*clamp(O.w,.02,.1), 1.4,.8, 0)
        - 8.*max(O,.03);
    O *= U.y+.1;
    O.r += O.g*.5;}






