void mainImage(out vec4 f,vec2 c)
{
    vec3  r = iResolution;
    vec2  a = c/r.y; 
    vec2  p = a - r.xy * .5 / r.y;
    float l = length(p);
    float t = abs(sin(iTime));
    vec2  d = (p/l * 9.)*sin(l*60.-iTime*9.)*.01 * smoothstep(1., .4, l / t) * smoothstep(0.1, 1., l * t);
    f = texture(iChannel0, a + d) + pow(d.x * 12. + .5, 8.);
}
