void mainImage(out vec4 l, in vec2 v)
{
	v = v / iResolution.xy - .5;v.x *= 2.;
	vec4 p = l + tan(iTime)*4., b = vec4(.3, .4, -.8, .5);

    for (int i=0; i < 36; i++) {
		l = abs(p) - 6.;l.xz *= mat2(b);l = abs(mod(l, 9.) - 4.);
		p += vec4(v,-1, 0) * (min(max(l.x, l.y)+l.y, max(l.y, l.z)-.5) - .7);
	}

	l = b.yzww * -p * .03;
}
