#define PI 3.141592

float hash(float n) {return fract(sin(n * 0.021) * 43758.5453123);}//from iq

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mo = iMouse.xy / iResolution.xy;
    mo -= 0.5;
    mo.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    float subd = 15.0;
    float radius = 0.5;
    vec3  color = vec3(0.0);

    float hsub = subd*2.0;
    vec2 grid = uv * subd;
    grid = vec2(floor(grid.x)/subd, floor(grid.y)/subd);
    vec2 gridC = vec2(grid.x + 1.0/hsub, grid.y+1.0/hsub);

    vec2 mDist = vec2(sin(time)*0.5, cos(time)*0.3) - grid;

    float weight = 0.0;
    if(length(mDist) < radius) {
        weight = smoothstep(0.0, 0.3, max(0.0, radius - length(mDist)));
    }

    float qbRadius = clamp(0.5*smoothstep(0.5, 0.1, length(mDist)), 0.2, 0.5);
    float qbWeight = 0.85*smoothstep(0.5, 0.1, length(mDist));   

    vec2 cellD = uv - gridC;
	float finalRadius = qbRadius/subd;
    float rings = 0.0;
    if(length(cellD) < qbRadius/subd) {
        float col = 0.0;
        col = smoothstep(1.0,0.9,length(cellD)/(qbRadius/subd));
        col -= 1.0*smoothstep(1.0,0.9,length(cellD)/(qbRadius/subd*qbWeight));

        rings += col;
    }

    color = 
        vec3(0.03) 
        + rings*vec3(grid.x+0.7, grid.y+0.7, 0.7)*0.1
        + rings*weight*vec3(grid.x+0.7, grid.y+0.7, 0.7)*2.0
        + weight*vec3(grid.x+0.5, grid.y+0.5, 0.5)*0.3;

    fragColor = vec4(color.rgb, 1.0);
}
