void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x += mod(uv.y, 2.0/iResolution.y) * 5.0;

    vec4 total = vec4(0);

    const float BlurAmount = 30.0;
    for (float blur = 0.0; blur < BlurAmount; blur++){
        vec2 offset = vec2((blur / BlurAmount) * 0.03, 0);

        vec4 color = texture(iChannel0, uv + offset);
        color.r *= 0.25;
        color.g *= 0.25;
        color.b *= 2.0;

        vec4 color2 = texture(iChannel0, uv + vec2(0.01, 0)+ offset);

        for (float i = 0.0; i < 40.0; i++){
            float pixel = i * 0.0001;
            vec4 temp = texture(iChannel0, uv + vec2(pixel, 0.0)+ offset);
            if (temp.b < color2.b){
                color2 = temp;   
            }
        }

        vec4 color3 = texture(iChannel0, uv - vec2(0.01, 0)+ offset);

        for (float i = 0.0; i < 40.0; i++){
            float pixel = i * 0.0001;
            vec4 temp = texture(iChannel0, uv - vec2(pixel, 0.0)+ offset);
            if (temp.b > color2.b){
                color3 = temp;   
            }
        }

        color2.rg *= 0.25;
        color2.b *= 2.0;

        color3.rg *= 0.25;
        color3.b *= 2.0;

        color += color2;
        color += color3;

        color.rg *= 0.5;
        color.b *= 1.5;

        color.r = smoothstep(1.0, 2.0, color.b);
        color.g = smoothstep(1.5, 2.0, color.b);
        total += color;
    }

    total /= BlurAmount;

	fragColor = total;
}
