const float jumpSpeed = 2.0;
const float edgeSharpness = 7.0;
const float jumpHeight = 0.3;
const float radius = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    vec2 position = vec2(0.5 * ratio, abs(sin(iTime * jumpSpeed)) * jumpHeight);
    position.y += radius;


    float distance = distance(position, uv);
    float ball = 1.0 - pow(distance * (1.0 / radius), edgeSharpness);

	fragColor = vec4(ball, 0, 0, 1.0);
}

#define PI 3.1415926535897932384626433832795

// Should be same values as in Image, is it really not possible to create a shared value?
const float jumpSpeed = 2.0;

vec2 mainSound( in int samp,float time)
{
    time += 0.07;
    time = mod( time, PI / jumpSpeed);

    return vec2( sin((2.0*PI)*440.0*time)*exp(-30.0*time) );
}
