// Based on LJ magics

vec3 ro,rd,mp;
float md;

mat2 r2d(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}
float soc(vec3 p){vec3 n=normalize(sign(p));return min(min(dot(p.xy,n.xy),dot(p.yz,n.yz)),dot(p.xz,n.xz));}

float map(vec3 p){
    p.xy*=r2d(iTime*.7);
    p.yz*=r2d(iTime*.5);
	p.xz*=r2d(iTime*.3);
    return soc(max(abs(p)-.2,1e-6))-.1*sqrt(2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    ro=vec3(uv,3.);
    rd=vec3(uv,-1.);
    mp=ro;
    for(int i=0;i<50;++i){
        md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
	fragColor = vec4(length(mp-ro)*.1);
}
