void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;

    vec4 color = vec4(xy.x/200.0*abs(sin(iTime)),xy.x/200.0*abs(cos(iTime)),xy.y/200.0*abs(tan(iTime)),1.0);


    if(xy.x - (10.0 * floor(xy.x/10.0)) < 5.0*abs(sin(iTime)) && xy.y - (10.0 * floor(xy.y/10.0)) > 5.0*abs(cos(iTime)))
    {
        color =  vec4(abs(sin(iTime)),abs(tan(iTime)),1.0,1.0);
    }
    if(xy.x - (10.0 * floor(xy.x/10.0)) > 5.0*abs(sin(iTime)) && xy.y - (10.0 * floor(xy.y/10.0)) < 5.0*abs(cos(iTime)))
    {
        color =  vec4(1.0,abs(sin(iTime)),abs(tan(iTime)),1.0);
    }

    if(sqrt( (xy.x-200.0)*(xy.x-200.0) + (xy.y-200.0*abs(sin(iTime)))*(xy.y-200.0*abs(sin(iTime)))) < 30.0)
    {
        color =  vec4(1.0*abs(sin(iTime)),1.0*abs(cos(iTime)),0.0,1.0);
    }

    fragColor = color;
}
