#define TWOPI 6.28318530718

vec3 tunnel(sampler2D sampler, in vec2 uv, in float time) {
    float r = length(uv);
    float a = time + atan(uv.y,uv.x);
    float b = time + atan(uv.y,abs(uv.x));

    vec2 uvL = vec2(1.0/r + time, a/3.1415927);
    vec2 uvR = vec2(1.0/r + time, b/3.1415927);

	return textureGrad(sampler, uvL, dFdx(uvR), dFdy(uvR)).xyz;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    float t = 0.01 * iTime;
    fragColor = vec4(tunnel(iChannel0, uv, t), 0.0) * length(uv);

}
