#define T texture(iChannel0, p.zx*.1 - t*.01
void mainImage( out vec4 o, vec2 c ) {
	vec4 p = vec4(c,0,0) / iResolution.xyxy - .5, d=p, e;
	for(float t=iTime, x, i=1.; i>0. ; i-=.04)
    {
        e = sin(p*6. + t);
        x = abs(p.y*10. + e.x*e.z - 7.) - (e=T) + T*2.)).x;
        o = .3/length(p.xy-vec2(sin(t),.4)) - e*i*i;
        p -= d*x*.1;
        if(x<.1) break;
     }
}

