void mainImage(out vec4 c, vec2 P) {
    c = vec4(.2);
    P = P / iResolution.xy*4. - 2.;    

    float T = iTime,
    a = P.y*sin(T) + T*2. + sin(P.y + T);

    for (int n = 0; n < 4; n++) {
        float A = cos(a), B = sin(a);

        if (P.x > A && P.x < -B) {
            vec2 uv = vec2((P.x - A)/(-B - A), P.y*.5);            
            c = texture(iChannel0, uv)*-(B + A)*(uv.x < 0.02 ? 1.5 : 1.);
        }

        a += 1.5707;
    }    
}
