renderpass.code:
// note that boxfilter(sin(kx)) = 2/ksin(k/2) * sin(kx) , so sampling equiv to boxfilter
// same for gaussianfilter http://www.wolframalpha.com/input/?i=int(sin(k*x)*exp(-1%2F2*((x-X)%2Fs)%5E2),x%3DX-1%2F2..X%2B1%2F2)

void mainImage( out vec4 O,  vec2 U )
{
    float
       // s = 1.2, v=10.,                      // for single circle ( click to see )
          s = 1.4, v=0.,                       // motionless
       // s = 1.5, v=10.,                      // with motion
          l = length(U-.5-iResolution.xy/2.);  // distance to center

    if ( iMouse.z>0. ) {                       // one single circle
        float l0 = 2.*s* floor(iResolution.y/4. /(2.*s) -1./4. ) + 1./4.; // radius
        v = 0.; 
        if ( abs(l-l0-s/2.) > s ) l = -s/2.; 
    };

	O = vec4(pow( .5+.5*sin( 3.141592654/s * l - v*iTime) , 1./2.2) );
}
