{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float col = texture(iChannel0, uv).x;
    col = 20.0 * col / float(iFrame);
    col = pow(col, 0.4545);
	fragColor = vec4(vec3(col),1.0);
}

#define A -1.4
#define B 1.6
#define C 1.0
#define D 0.7

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    if(uv.y > 0.01) {
        discard;
    }
   	vec2 coord;
    if(iFrame == 0) {
        coord = uv * 2.0 - 1.0;
    } else {
        coord = texture(iChannel0, uv).xy;
        coord = vec2(sin(A*coord.y) + C*cos(A*coord.x),
                     sin(B*coord.x) + D*cos(B*coord.y));
    }
    fragColor = vec4(coord.xy, 0, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame < 10) {
        fragColor = vec4(0);
        return;
    }
    vec2 uv = fragCoord / iResolution.xy;
    float contrib = 0.0;
    for(int x = 0; x < 256; x += 1) {
        vec2 coords = texture(iChannel0, vec2(float(x) / 256.0,0)).xy;
        coords -= uv * 4.0 - 1.8;
        coords *= 512.0;
        contrib += 1.0/(dot(coords, coords) + 1.0);
    }
    vec4 prev = texture(iChannel1, uv);
    float total = prev.r + contrib;
    fragColor = vec4(vec3(total), 1);
}
