#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 p = uv - vec2(sin(.5*t)+0.5, cos(.5*t)-.5);
    vec2 q = uv - vec2(cos(.5*t)+0.5, sin(.5*t)-.5);
    fragColor = texture(iChannel0,vec2(length(p), p));
    fragColor += texture(iChannel1,vec2(length(q), length(q*q)));
    fragColor = mix( vec4(1.3,1.0,0.4, .5),fragColor , length(p+0.5*uv));
}
