///////////////////////////////////////////////////////////////////////

#define PI 3.14159265359
#define TWO_PI 6.28318530718

//  Function from IÃ±igo Quiles 
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0), 6.0)-3.0)-1.0, 0.0, 1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float u_time = iTime;
  vec2 u_mouse = iMouse.xy;
  vec2 u_resolution = iResolution.xy; //. // Note: Could use #define u_time iTime \n instead of this in the future.


	vec2 st = fragCoord.xy/u_resolution.xy; // pixel_n

        // Quick Aspect Ratio Fix
    float aspect = u_resolution.x / u_resolution.y;
    st.x *= aspect;

    // "HSB was originally designed to be represented in polar coordinates (based on the angle and radius) instead of cartesian coordinates (based on x and y)."
    vec3 color = vec3(0.0);

    // Using Polar Coordinates instead of Cartesian
    vec2 vectorToCentre = vec2(0.5) - st;
    float angle = atan(vectorToCentre.y, vectorToCentre.x); // function is atan(y,x) , not x,y
    float distanceToCentre = length(vectorToCentre);
    float radius = distanceToCentre * 2.0;
    // Bend Hue based on Time and Angle
    angle = angle + u_time*5.0 - radius*2.5;

    // Set Color
    vec3 hue = vec3(angle/TWO_PI+0.5, radius, 1.0);
    color = hsb2rgb(hue);

    // Set Alpha
    float alpha = smoothstep(0.5, 0.46, distanceToCentre);

    // Render Color
	fragColor = vec4(color*alpha, 1.0);

    // fragColor = gl_FragColor; // .
}
