void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y+=0.3;
    uv.y*=0.6;
    vec4 b = texture(iChannel0,uv);
    float k  = floor(b.x*2.0)*2.0;
    float v  = b.x*0.5;
    if(k > 0.91 || (b.x > 0.2 && b.x < 0.8)){ 
        discard;
    }
    if(mod(float(iFrame),2.0)<1.0){
        v = 1.0-v;
    }
    v = floor(v*2.0);
    fragColor = vec4(v);
}
