#define PI 3.1415926
#define TWOPI = 6.2831853

vec2 angleRadius(vec2 uv) {
    float twoPi = 6.2831853;
	float anglePixel = atan(uv.y, uv.x) / twoPi;
    float lengthPixel = length(uv);

    return vec2(.0, lengthPixel);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float radius = 0.3;

    //float inCircle = step(radius, angleRadius(uv).y);
    float inCircleAA = smoothstep(radius, radius + 0.005, angleRadius(uv).y); //AA version
    float smallCircleAA = smoothstep(radius / 2.0, radius / 2.0 + 0.005, angleRadius(uv).y); //AA version
    vec3 col = 1.0 - vec3(inCircleAA);
    vec3 col_2 = 1.0 - vec3(smallCircleAA);

    fragColor = vec4(col - col_2,1.0);
}
