float text (vec4 box, vec2 character, vec2 uv){
    if(texture(iChannel0, (vec2((uv.x-box.x)/box.z, (uv.y-box.y)/box.w)+character)/16.).r>0.4&&uv.x>box.x&&uv.y>box.y&&uv.x<box.x+box.z&&uv.y<box.y+box.w){
        //this if statement checks these things:
        //in box?
        //hitting a letter and not a blue/green spot?
        return 1.;
    } else {
        return 0.;
    }
}
//nums start on row 12

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ov = uv;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    //coords done

    float num = mouse.x*100.;//THE NUM
    int digits = 5;//have to include decimal point
	//get digits from number
    float tens = floor(mod(num/10., 10.));
    float ones = floor(mod(num, 10.));
    float tenths = floor(mod(num*10., 10.));
    float hundredths = floor(mod(num*100., 10.));
    //i'm starting to see a pattern here . . .

    //setup text stuff
    vec3 col = vec3(0.); // set col as black
    vec2 box = vec2(0.2, 0.2);//size of each letter
    vec2 move = vec2(box.x*0.44, 0.0);//amount to move to place each new letter
    vec2 stamp = vec2(-move.x*float(digits)/2.-box.x*0.28, -box.y/2.);//where to "stamp" the letter
    float total = 0.;

    float ten = text(vec4(stamp, box), vec2(tens, 12), uv);
    stamp+=move;
    total = max(total, ten);
    float one = text(vec4(stamp, box), vec2(ones, 12), uv);
    stamp+=move;
    total = max(total, one);
    //decimal point
    float decimal = text(vec4(stamp, box), vec2(14, 13), uv);
    stamp+=move;
    total = max(total, decimal);
    float tenth = text(vec4(stamp, box), vec2(tenths, 12), uv);
    stamp+=move;
    total = max(total, tenth);
    float hundredth = text(vec4(stamp, box), vec2(hundredths, 12), uv);
    stamp+=move;
    total = max(total, hundredth);

    total = max(total, tenth);

    col = vec3(total);

    //col = texture(iChannel0, ov).rrr;//for testing
    // Output to screen
    fragColor = vec4(col,1.0);
}
