void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv = vec2(uv.x + texture(iChannel0, uv.xy + sin(iTime * 0.1 + uv.y)).x * 0.9 , uv.y);

	fragColor = vec4(
		(sin(iTime + uv.x) + 1.0) / 2.0 * texture(iChannel1, uv.xy + sin(uv.x)).x, 
        (sin(iTime * 0.3 + uv.x) + 1.0) / 2.0 * texture(iChannel1, uv.xy + sin(uv.x)).x, 
        (sin(iTime * 0.5 + uv.y) + 1.0) / 2.0 * texture(iChannel1, uv.xy + sin(uv.x)).x, 
        0
    );

}
