void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cval = texture(iChannel0,fragCoord / iResolution.xy * 0.25 ).x;
	fragColor = vec4(0.2,0.3,0.6,1.0)*cval*cval;
}

float read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 

    if(iFrame==0 || mod(float(iFrame),340.0) ==0.0)
    {
        fragColor.x=(fract(dot(sin(fragCoord*vec2(157.1,113.7)),
                               vec2(353.17+iTime,383.093-iTime)))>0.33)?0.0:1.0;
    }
    else
    {
		float u = 0.0;
    	u+=sin(read(fragCoord, vec2(1.0,0.0)));
        u+=sin(read(fragCoord, vec2(1.0,1.0)));
        u+=cos(read(fragCoord, vec2(-1.0,1.0)));
        u+=cos(read(fragCoord, vec2(-2.0,1.0)));

        fragColor.x = (sin(u) + 1.0) * 0.77;
    }
}
