/*
	------------------------------------
	OldSchool FX : RotoZoom + Distorsion
	------------------------------------

	A quick shader to remind me this oldschool effect I wrote on my AMIGA ... long long time ago in ASM.

	Francois 'CoyHot' Grassard - 2018
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = ((fragCoord.xy / iResolution.xy)-0.5)/vec2(iResolution.y / iResolution.x, 1);

    float pix = 50.*sin(iTime);
    float angle = iTime/2.;

    // Slide and distort
    uv.y += cos (uv.y)+sin (uv.x);
    uv.x += iTime/250.;


    // Pixelate
    uv *= pix;
    uv =  floor (uv);
    uv /= pix;


  	// Zoom
	uv *= 5.;
    uv *= sin(iTime);


  	// 2D Rotate   
    uv *= mat2(cos(angle),-sin(angle),sin(angle),cos(angle));


    // Catch an image using the tweaked UV
    vec4 picture = texture(iChannel0,vec2(uv.x/10.,uv.y/2.5));


    // Output to screen
    fragColor = picture;
}
