void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.xy;
    vec2 uv3 = fragCoord/iResolution.xy;
    vec2 ouv = uv;

    float fT = iTime;
    float amplitude = 0.9 + 0.2 * sin(fT);

	uv.x += sin(ouv.y*9.161 + fT)*0.003 * amplitude;
    uv.y += sin(ouv.x*6.363 + fT)*0.003 * amplitude;

    uv2.x -= sin(ouv.y*9.861 + fT)*0.003 * amplitude;
    uv2.y -= sin(ouv.x*7.395 + fT)*0.003 * amplitude;

    uv3.x += sin(ouv.y*15.161 + fT)*0.02;
    uv3.y -= sin(ouv.x*9.363 + fT)*0.02;


    //float am = 0.5 + 0.5 * (sin(texture(iChannel2, uv3 / 4.).r + fT)); 


    float am = ((texture(iChannel2, mix(uv2, uv3, 0.5 + 0.5 * sin(iTime)) * 0.1 + vec2(iTime * 0.01, iTime * 0.02)).r) * 1. ) + 
        ((texture(iChannel2, mix(uv2, uv3, 0.5 + 0.5 * sin(iTime + 121.)) * 0.2).r) * 0.5) +
        ((texture(iChannel2, mix(uv2, uv3, 0.5 + 0.5 * sin(iTime - 1241.51)) * 0.4).r) * 0.25) + 
        ((texture(iChannel2, mix(uv2, uv3, 0.5 + 0.5 * sin(iTime + 115.)) * 0.8).r) * 0.125) +
        ((texture(iChannel2, mix(uv, uv3, 0.5 + 0.5 * sin(iTime - 74.61)) * 1.6).r) * 0.0625);

    am *= 0.5;

    /*if(am > 0.5)
    {
        am = 1.;
    }
    else
    {
        am = 0.;
    }*/

    am = pow(am, 2.) * 1.4;

    vec3 col = mix(texture(iChannel0, uv).rgb, texture(iChannel1, uv2).rgb, am);
    //col = vec3(am);

    fragColor = vec4(col,1.0);
}
