void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
	vec3 base = texture( iChannel0, uv ).xyz;

    //sine wave the blur level
    float level = (sin(iTime*2.0)*0.5+0.5)*6.0;

    //sample mips for blurred source
    vec3 blur = textureLod( iChannel0, uv, level ).xyz;

    //filter is rescaled difference from blur/unblur
    vec3 filtered = ((base-blur)*0.5+0.5) * 2.0;

	//mix filtered/unfiltered for comparison
 	vec3 col = mix( base, filtered * base, uv.x > 0.5 ? 1.0 : 0.0);   

    //draw line
    col += (uv.x > 0.5 ? 1.0 : 0.0) * (uv.x > 0.505 ? 0.0 : 1.0);

    fragColor = vec4(col,1.0);
}
