void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
    float tt = texture(iChannel0, uv).x;

    tt = pow(tt, 0.4 * nuv.x);

	fragColor.x = sin(10.0 * (uv.x * sin(t * 0.3) + uv.y * cos(t * 0.01)) + t);
    fragColor.y = sin(nuv.x * 0.3 * sin(t * 1.3) + nuv.y * 0.4 * sin(t * 0.2));

    float cx = nuv.x * 0.3 * sin(t * 0.2);
    float cy = nuv.y * 0.5 * cos(t * 0.4) * tt * 0.1;
    fragColor.z = sin(sqrt(520.0 * (cx * cx + cy * cy) + 1.0) + t * 3.0);

    fragColor.x = fragColor.x + fragColor.z;
    fragColor.y = fragColor.x * fragColor.x * 0.3 - fragColor.z * 0.2;
    fragColor.z = fragColor.x * 0.2;

    fragColor.x -= length(nuv) * 4.0;
    fragColor.y += cy * nuv.x;
    fragColor.z *= cx;

    fragColor.xyz += vec3(0.1, 0.1, 0.4);
    fragColor.xyz += length(fragColor.xyz) * 0.1 * tt;
    fragColor.x += fragColor.x * tt * 2.0;
}
