float df(vec2 p)
{
    float r = dot(p,p) * 0.02 * cos(iTime * 20.);

	p *= mat2(cos(r), -sin(r), sin(r), cos(r));

	return max(abs(p.x)+p.y, abs(p.y*p.x)) + length(p)*0.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvt = gl_FragCoord.xy / iResolution.xy;
    uvt.y += iTime * 1.;

	vec2 uvc = (gl_FragCoord.xy *2. - iResolution.xy) / iResolution.y * 3.32;

	float d = df(uvc);

	float c = 1.-mix(0., 1., d);

	c = sqrt(c);

	fragColor = vec4(c);

    vec4 bg = texture(iChannel0, uvt);

    bg.b *= 2.0 + sin(iTime*20.0+ gl_FragCoord.y*0.1)*0.5; // thanks to Dain

    if (d > .9)
        fragColor = bg;
}
