// Inspired by https://www.shadertoy.com/view/MslcD4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -=.5;
    uv.x*=iResolution.x/iResolution.y;
    float c=1.;
    for(int i=0;i<30;++i){
        float y=.7-1.4*float(i)/30.+.1*sin(iTime+uv.x*10./(1.+exp(-.1*pow(abs(float(i)-15.),2.)))+float(i)*6.2832/4.)*exp(-5.*pow(length(uv),2.));
        c=mix(c,1.-exp(-50.*(y-uv.y)),smoothstep(uv.y,uv.y+2./iResolution.y,y));
    }
	fragColor = vec4(c);
}
