void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 15. - 5.;

	uv = sin(uv) * 0.5 + 0.5;

	uv.x = uv.x / uv.y * fract(iTime/40.);

	vec2 a = sin(uv * iTime/2.) * 0.5 + 0.5;
	vec2 b = cos(uv / 2.) * 0.5 + 0.5;

	vec2 c = all(lessThan(a,  b)) ? a : b; 
	c = (sin(c*a)*0.5+0.5) * (tan(c/b)*0.5+0.5);
	c = c / uv.x*uv.y;

	float m = (c.x + c.y + (uv.x * uv.y)) * (fract(sin(iTime / 7.)*1.5));
	//m = m / c.x /c.y;
	fragColor = vec4(m, m, m,1.0);
}
