void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Variables to be fed into the formula
	float a = 1.0 + 0.5 * cos(iTime/16.0);
	float b = 1.0 + 0.1 * cos(iTime/8.0);
	float m = 15.0 + 7.5 * cos(iTime/4.0);
	float n1 = 5.0 + 2.0 * cos(iTime/2.0);
	float n2 = 6.0 + 2.0 * cos(iTime);
	float n3 = 24.0 + 10.0 * sin(iTime);

	vec2 uv = fragCoord.xy / iResolution.xy;

	// As the center is assumed to be 0,0 we'll shift the uv vector accordingly
	vec2 sf = uv - 0.5;

	// r of the current point
	float pr = sqrt(sf.x*sf.x + sf.y*sf.y);
	// Angle (Phi) of the current point
	float f = atan(sf.y/sf.x);

	// The formula itself. Division by 10.0 added to scale the result down a bit.
	float r = pow((pow(abs(cos(m*f/4.0)/a),n2) + pow(abs(sin(m*f/4.0)/b), n3)), -(1.0/n1)) / 10.0;

	// Output with coloring by relative radius
	fragColor = (pr <= r) ? vec4(1.0-pr/r, 1.0-(pr/r/2.0), 1.0-pr/r, 1.0) : vec4(0.1, 0.1, 0.1, 1.0);
}
