#define PI 3.14159;

float circle(vec2 uv, float width) {
	float add = fract(iTime);
    float cOuter = step(length(uv), width + add);
    float cInner = step(.0 + add, length(uv));

    float c = cOuter * cInner;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(.0, .0, .0);

    float e = exp(fract(iTime));
    float c = circle(uv * e, .02);

    color = vec3(c);

	fragColor = vec4(color, 1.0);
}
