void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float res1 = (100.0-1.0) * ((sin(iTime)+1.0)/2.0) + 1.0;//sinus
    float res2 = (30.0-1.0) * abs(mod(iTime * 0.1,2.0)-1.0);//linear bounce
    float res = res2;

    vec2 uv = vec2(floor(fragCoord.x / res) * res, floor(fragCoord.y / res) * res) / iResolution.xy;

    vec3 color = vec3(uv,1);

	fragColor = vec4(color,1.0);
}
